import React, { useState, useEffect } from 'react';
import  ProductCss from './Products.module.css'
import Dropdown from 'react-bootstrap/Dropdown'





// this function fetches the products from the database and displays them on the page.

function Products() {
    const [items, setItems] = useState([]);

    useEffect(() => {
        fetch('/product/items')
            .then(res => res.json())
            .then(data => {
                setItems(data);
                console.log('Fetched items:', data);
            })
            .catch(error => console.error('Error fetching items:', error));
    }, []);

    return (
      <>
      <div className={ProductCss.filterContainer}>

      <Dropdown className={ProductCss.Dropdown}>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
       Filter Products
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item href="#/action-1">High to Low</Dropdown.Item>
        <Dropdown.Item href="#/action-2">Low to High action</Dropdown.Item>
        <Dropdown.Item href="#/action-3">A-Z</Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>







                  <button>Daily</button>
              <button>Track</button>
              <button>Drag</button>
                  </div>
        <div className={ProductCss.productContainer}>
            {items.map((item, i) => (
                <div key={i}>
             <div className={ProductCss.productContent} >
             <h5>{item.product_name}</h5>
                   <div className={ProductCss.imgContainer}>
                   <img src={item.product_image} alt={item.product_name} className={ProductCss.productImage}/>
                   </div>
                   <div className={ProductCss.textContainer}>
                   <p>{item.product_description}</p>
                   </div>
                    <p>Price:{item.product_price} $USD</p>
                    <div className={ProductCss.btnContainer}>
                      <button className={ProductCss.addBtn}>Add to cart</button>
                    </div>
             </div>
                </div>
            ))}
        </div>
        </>
    );
}

export default Products;
